import React, { Component } from 'react';
import Web3 from 'web3';
import Identicon from 'identicon.js';
import './App.css';
import SocialNetwork from '../abis/SocialNetwork.json'
import Navbar from './NavBar.js'
import Main from './Main'
import { create } from 'ipfs-http-client';

const projectId = '31a124d4983148898ba3b98064780201';
const projectSecret = 'JCPzeDEM2qBKHlMG1ENYSI7cjjiDXuQMbSYa84jEar1YJ1/YjlMDPg';
const auth = 'Basic ' + Buffer.from(projectId + ':' + projectSecret).toString('base64');

const ipfs = create('https://ipfs.infura.io:5001/api/v0');

class App extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    // Network ID
    const networkId = await web3.eth.net.getId()
    const networkData = SocialNetwork.networks[networkId]
    if(networkData) {
      const socialNetwork = web3.eth.Contract(SocialNetwork.abi, networkData.address)
      this.setState({ socialNetwork })
      const postCount = await socialNetwork.methods.postCount().call()
      this.setState({ postCount })
      // Load Posts
      for (var i = 1; i <= postCount; i++) {
        const post = await socialNetwork.methods.posts(i).call()
        this.setState({
          posts: [...this.state.posts, post]
        })
      }
      // Sort posts. Show highest tipped posts first
      this.setState({
        posts: this.state.posts.sort((a,b) => b.tipAmount - a.tipAmount )
      })
      this.setState({ loading: false})
    } else {
      window.alert('SocialNetwork contract not deployed to detected network.')
    }
  }

  constructor(props) {
    super(props);
    this.state = {
      account: '',
      socialNetwork: null,
      postCount: 0,
      posts: [],
      buffer: null,
      content: ''
    };

    this.captureFile = this.captureFile.bind(this);
    this.uploadImage = this.uploadImage.bind(this);
    this.createPost = this.createPost.bind(this);
  }

  captureFile(event) {
    event.preventDefault();
    const file = event.target.files[0];
    const reader = new window.FileReader();
    reader.readAsArrayBuffer(file);
    reader.onloadend = () => {
      this.setState({ buffer: Buffer(reader.result) });
    };
  }

  // createPost(content) {
  //   this.setState({ loading: true })
  //   this.state.socialNetwork.methods.createPost(content).send({ from: this.state.account })
  //   .once('receipt', (receipt) => {
  //     this.setState({ loading: false })
  //   })
  // }

  tipPost(id, tipAmount) {
    this.setState({ loading: true })
    this.state.socialNetwork.methods.tipPost(id).send({ from: this.state.account, value: tipAmount })
    .once('receipt', (receipt) => {
      this.setState({ loading: false })
    })
  }

  // constructor(props) {
  //   super(props)
  //   this.state = {
  //     account: '',
  //     socialNetwork: null,
  //     postCount: 0,
  //     posts: [],
  //     loading: true
  //   }

  //   this.createPost = this.createPost.bind(this)
  //   this.tipPost = this.tipPost.bind(this)
  // }

  async uploadImage() {
    const result = await ipfs.add(this.state.buffer);
    return result.path;
  }

  async createPost(content) {
    const imageHash = await this.uploadImage();
    this.state.socialNetwork.methods.createPost(content, imageHash).send({ from: this.state.account })
      .once('receipt', (receipt) => {
        this.setState({ content: '' });
      });
  }

  render() {
    return (
      <div>
        {/* <Navbar account={this.state.account} />
        { this.state.loading
          ? <div id="loader" className="text-center mt-5"><p>Loading...</p></div>
          : <Main
              posts={this.state.posts}
              createPost={this.createPost}
              tipPost={this.tipPost}
            />
        } */}

        <Navbar account={this.state.account} />
          <div className="container-fluid mt-5">
            <div className="row">
              <main role="main" className="col-lg-12 ml-auto mr-auto" style={{ maxWidth: '500px' }}>
                <div className="content mr-auto ml-auto">
                  <p>&nbsp;</p>
                  <h2>Share Image</h2>
                  <form onSubmit={(event) => {
                    event.preventDefault();
                    const content = this.postContent.value;
                    this.createPost(content);
                  }}>
                    <input type='file' onChange={this.captureFile} />
                    <div className="form-group mr-sm-2">
                      <input
                        id="postContent"
                        type="text"
                        ref={(input) => { this.postContent = input }}
                        className="form-control"
                        placeholder="What's on your mind?"
                        required />
                    </div>
                    <button type="submit" className="btn btn-primary btn-block">Share</button>
                  </form>
                  <p>&nbsp;</p>
                  {this.state.posts.map((post, key) => {
                    return (
                      <div className="card mb-4" key={key} >
                        <div className="card-header">
                          <img
                            className='mr-2'
                            width='30'
                            height='30'
                            src={`data:image/png;base64,${new Identicon(post.author, 30).toString()}`}
                          />
                          <small className="text-muted">{post.author}</small>
                        </div>
                        <ul id="postList" className="list-group list-group-flush">
                          <li className="list-group-item">
                            <p className="text-center"><img src={`https://ipfs.infura.io/ipfs/${post.imageHash}`} style={{ maxWidth: '420px' }} /></p>
                            <p>{post.content}</p>
                          </li>
                        </ul>
                      </div>
                    )
                  })}
                </div>
              </main>
            </div>
          </div>
      </div>
    );
  }
}

export default App;
